{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}

<div class="main-container">
  <style>
    html, body { margin:0; padding:0; font-family: Arial, sans-serif; font-size: 13px; }
    .main-container { padding: 8px; }
    .heading-label { font-size:20px; font-weight:700; text-align:center; margin-bottom:8px; }
    .section { margin-bottom: 7px; }
    .section-title { font-size:14px; font-weight:600; border-bottom:1px solid #000; margin-bottom:2px; }
    .info-table, .cost-table, .items-table { width:100%; border-collapse:collapse; }
    .info-table td, .cost-table td { padding:2px 4px; font-size:13px; }
    .items-table th, .items-table td { padding:0px 4px; font-size:13px; }
    .info-table, .cost-table { line-height:0.6; }
    .items-table tr { line-height:1.0; }
    .items-table th,
    .items-table .total-row {
      background: #f5f5f5;
      border-bottom: 1px solid #ccc;
      color: #222;
      font-weight: 700;
    }
    .rt { text-align:right; }
    .cn { text-align:center; }
    .bold { font-weight:600; }
    .job-description { 
      line-height: 1.4; 
      padding: 6px 4px; 
      white-space: pre-wrap; 
      word-wrap: break-word; 
      max-width: 100%; 
    }
    .job-description-section { 
      margin-bottom: 10px; 
    }
  </style>

  <div class="heading-label">Quotation Cost Sheet</div>

  <div class="section">
    <table class="info-table">
      <tr>
        <td><strong>Quotation No.:</strong> {{ doc.name }}</td>
        <td class="rt"><strong>Date:</strong> {{ frappe.utils.formatdate(doc.transaction_date, "dd-MMM-yy") }}</td>
      </tr>
      <tr>
        <td><strong>Customer:</strong> {{ doc.customer_name }}</td>
        <td class="rt"><strong>Job Type:</strong> {{ doc.job_type or "-" }}</td>
      </tr>
    </table>
  </div>

  <div class="section job-description-section">
    <div class="job-description">
      <strong>Job Description:</strong> {{ doc.job_description or "-" }}
    </div>
  </div>

  <div class="section" style="margin-bottom: 4px;">
    <div class="section-title">Cost & Revenue Summary</div>
    <table class="cost-table" style="margin-bottom: 0;">
      <tr>
        <td>Expected Revenue</td>
        <td class="rt"><span class="bold">{{ frappe.utils.fmt_money(doc.est_revenue or 0, currency=doc.currency, format="#,##0.00") }}</span></td>
      </tr>
      <tr>
        <td>Expected Cost</td>
        <td class="rt"><span class="bold">{{ frappe.utils.fmt_money(doc.est_cost or 0, currency=doc.currency, format="#,##0.00") }}</span></td>
      </tr>
      <tr>
        <td>Expected Profit (Margin)</td>
        <td class="rt"><span class="bold">{{ frappe.utils.fmt_money(doc.est_profit or ((doc.est_revenue or 0) - (doc.est_cost or 0)), currency=doc.currency, format="#,##0.00") }}</span></td>
      </tr>
      <tr>
        <td>Margin %</td>
        <td class="rt"><span class="bold">
          {% if doc.est_revenue %}
            {{ (((doc.est_profit or ((doc.est_revenue or 0) - (doc.est_cost or 0))) / (doc.est_revenue or 1)) * 100) | round(2) }}%
          {% else %}
            0%
          {% endif %}
        </span></td>
      </tr>
    </table>
  </div>

  <div class="section">
    <div class="section-title">Item Breakdown</div>
    <table class="items-table">
      <thead>
        <tr>
          <th style="width:30%">Description</th>
          <th style="width:20%">Supplier</th>
          <th class="rt" style="width:15%">Revenue</th>
          <th class="rt" style="width:15%">Cost</th>
          <th class="rt" style="width:15%">Margin</th>
        </tr>
      </thead>
      <tbody>
        {% for item in doc.items %}
        <tr>
          <td>{{ item.item_name }}</td>
          <td>{{ item.supplier or "-" }}</td>
          <td class="rt">{{ frappe.utils.fmt_money(item.amount or 0, currency=doc.currency, format="#,##0.00") }}</td>
          <td class="rt">{{ frappe.utils.fmt_money(item.cost_amount or 0, currency=doc.currency, format="#,##0.00") }}</td>
          <td class="rt">
            {{ frappe.utils.fmt_money((item.amount or 0) - (item.cost_amount or 0), currency=doc.currency, format="#,##0.00") }}
          </td>
        </tr>
        {% endfor %}
        <tr class="total-row">
          <td class="bold" colspan="2">Total</td>
          <td class="rt bold">
            {{ frappe.utils.fmt_money(doc.items | sum(attribute='amount'), currency=doc.currency, format="#,##0.00") }}
          </td>
          <td class="rt bold">
            {{ frappe.utils.fmt_money(doc.items | sum(attribute='cost_amount'), currency=doc.currency, format="#,##0.00") }}
          </td>
          <td class="rt bold">
            {{ frappe.utils.fmt_money((doc.items | sum(attribute='amount')) - (doc.items | sum(attribute='cost_amount')), currency=doc.currency, format="#,##0.00") }}
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <div style="margin-top:20px; font-size:12px; color:#888;">
    <em>For internal use only. Not for customer distribution.</em>
  </div>
</div>

{%- set currency = doc.company_currency or frappe.defaults.get_default('currency') -%}
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8">
    <style>
        /* Base */
        body {
            font-family: 'Courier New', monospace;
            font-size: 11px;
            line-height: 1.2;
            margin: 0;
            padding: 5px;
            width: 72mm; /* fits 80mm paper with margins */
            color: #000;
        }

        .receipt-container { width: 100%; }

        /* Header */
        .header {
            text-align: center;
            margin-bottom: 8px;
            border-bottom: 1px solid #000;
            padding-bottom: 6px;
        }
        .company-name { font-weight: bold; font-size: 13px; margin-bottom: 2px; }
        .receipt-title { font-weight: bold; font-size: 12px; margin-top: 2px; }

        /* Details */
        .details-section { margin: 7px 0; font-size: 10px; }
        .detail-row { display: flex; justify-content: space-between; margin: 2px 0; word-wrap: break-word; }
        .detail-label { font-weight: bold; min-width: 75px; }
        .detail-value { flex: 1; text-align: right; word-break: break-word; }

        /* Amount in words */
        .amount-words { margin: 8px 0; padding: 4px; border: 1px solid #000; font-size: 10px; }

        /* Accounts table (company copy only) - Debit only */
        .accounts-table { width: 100%; border-collapse: collapse; margin: 8px 0; font-size: 9px; }
        .accounts-table th, .accounts-table td { border: 1px solid #000; padding: 2px 4px; vertical-align: top; word-break: break-word; }
        .accounts-table th { font-weight: bold; text-align: center; background: #f0f0f0; }
        .amount-col { text-align: right !important; min-width: 60px; }

        /* Signature using table for reliable two-column layout */
        .signature-section {
            margin-top: 12px;
            border-top: 1px solid #000;
            padding-top: 8px;
            font-size: 10px;
            width: 100%;
        }
        .sig-table {
            width: 100%;
            border-collapse: collapse;
            margin-top: 6px;
        }
        .sig-table td {
            vertical-align: top;
            padding: 6px 8px;
        }
        /* REDUCED SPACING BETWEEN THE TWO SIGNATURE ROWS */
        .sig-table tr:first-child td {
            padding-bottom: 2px;
        }
        .sig-label { font-weight: bold; margin-bottom: 6px; display:block; }
        .sig-line { border-bottom: 1px solid #000; height: 20px; margin-bottom: 4px; width:100%; display:block; }
        .sig-small-note { text-align: center; font-size: 8px; margin-top: 2px; }

        /* Cut line and copy label */
        .cut-line { border-top: 1px dashed #000; margin: 6px 0 10px 0; }
        .copy-label { font-size: 10px; font-weight: bold; text-align: center; margin-bottom: 4px; }

        /* Copy behaviour */
        .receipt-copy { page-break-inside: avoid; }
        .receipt-copy.company { /* if printing to A4 and you want separate pages */ }
        .receipt-copy.recipient { }

        @media print {
            body { margin: 0; padding: 2px; }
            .receipt-container { page-break-after: always; }
        }
    </style>
</head>
<body>
    <div class="receipt-container">

        {%- macro render_header_and_details(show_accounts=True, copy_label=None, show_signature=True) -%}
        <div class="receipt-copy {{ 'company' if show_accounts else 'recipient' }}">
            <!-- Header -->
            <div class="header">
                <div class="company-name">{{ doc.company }}</div>
                <div class="receipt-title">CASH PAYMENT VOUCHER</div>
            </div>

            {% if copy_label %}
            <div class="copy-label">{{ copy_label }}</div>
            {% endif %}

            <!-- Details -->
            <div class="details-section">
                <div class="detail-row">
                    <span class="detail-label">Date:</span>
                    <span class="detail-value">
                        {{ frappe.utils.formatdate(doc.posting_date, "dd/mm/yyyy") }}
                    </span>
                </div>

                <div class="detail-row">
                    <span class="detail-label">Entry Type:</span>
                    <span class="detail-value">{{ doc.voucher_type or "Journal Entry" }}</span>
                </div>

                <div class="detail-row">
                    <span class="detail-label">Reference:</span>
                    <span class="detail-value">{{ doc.name }}</span>
                </div>

                {% if doc.user_remark %}
                <div class="detail-row">
                    <span class="detail-label">Details:</span>
                    <span class="detail-value">{{ doc.user_remark }}</span>
                </div>
                {% endif %}

                <div class="detail-row total-amount">
                    <span class="detail-label">Amount:</span>
                    <span class="detail-value">
                        {{ frappe.utils.fmt_money(doc.total_credit, currency=currency) }}
                    </span>
                </div>
            </div>

            <!-- Amount in Words -->
            <div class="amount-words">
                <div class="field-label">Amount in Words:</div>
                <div>{{ frappe.utils.money_in_words(doc.total_credit, currency) }}</div>
            </div>

            {%- if show_accounts %}
            <!-- Accounts Table (only for company copy) - Debit only -->
            <table class="accounts-table">
                <thead>
                    <tr>
                        <th style="width: 70%;">Account</th>
                        <th style="width: 30%;">Debit</th>
                    </tr>
                </thead>
                <tbody>
                    {# show only rows that have a debit amount to avoid zero rows #}
                    {% for account in doc.accounts if account.debit %}
                    <tr>
                        <td>{{ account.account }}</td>
                        <td class="amount-col">
                            {{ frappe.utils.fmt_money(account.debit, currency=currency) }}
                        </td>
                    </tr>
                    {% endfor %}
                </tbody>
            </table>
            {%- endif %}

            {%- if show_signature %}
            <!-- Signature Section (company copy) - table based 2x2 layout -->
            <div class="signature-section" role="group" aria-label="Signature section">
                <table class="sig-table" role="presentation">
                    <tr>
                        <td style="width:50%;">
                            <span class="sig-label">Received by:</span>
                            <span class="sig-line"></span>
                            <div class="sig-small-note">Name</div>
                        </td>
                        <td style="width:50%;">
                            <span class="sig-label">Signature:</span>
                            <span class="sig-line"></span>
                            <div class="sig-small-note">Signature</div>
                        </td>
                    </tr>
                    <tr>
                        <td>
                            <span class="sig-label">ID No:</span>
                            <span class="sig-line"></span>
                        </td>
                        <td>
                            <span class="sig-label">Cell No:</span>
                            <span class="sig-line"></span>
                        </td>
                    </tr>
                </table>
            </div>
            {%- endif %}

        </div>
        {%- endmacro %}

        <!-- Render Company Copy (full, with accounts table and signature) -->
        {{ render_header_and_details(show_accounts=True, copy_label="Company Copy", show_signature=True) }}

