            <img src="{{ company_image }}" style="width: 130px; height: 50px; object-fit: contain; display: block; margin: 0; padding: 0;">
          {% endif %}
        </td>
        <td style="text-align: right; margin: 0; padding: 0; vertical-align: top; line-height: 1.2;">
          <div style="font-size: 13px; font-weight: 700; margin: 0 0 2px 0; color: #000;">
            {{ doc.company }}
          </div>

          {% set address = frappe.get_all("Address", filters={"link_doctype": "Company", "link_name": doc.company}, fields=["address_line1", "address_line2", "city", "country"], limit=1) or [] %}
          {% if address %}
            <div style="margin: 0 0 2px 0; font-size: 11px; line-height: 1.2; color: #000;">
              {{ address[0].address_line1 or "" }}{% if address[0].address_line2 %}, {{ address[0].address_line2 }}{% endif %}<br>
              {{ address[0].city or "" }}{% if address[0].country %}, {{ address[0].country }}{% endif %}
            </div>
          {% endif %}

          <div style="margin: 0; font-size: 11px; line-height: 1.2; color: #000;">
            {% set phone = frappe.db.get_value("Company", doc.company, "phone_no") %}
            {% set email = frappe.db.get_value("Company", doc.company, "email") %}
            {% set tax_id = frappe.db.get_value("Company", doc.company, "tax_id") %}

            {% if phone %}{{ phone }}{% endif %}{% if phone and email %} | {% endif %}{% if email %}{{ email }}{% endif %}<br>
            {% if tax_id %}Tax ID: {{ tax_id }}{% endif %}
          </div>
        </td>
      </tr>
    </tbody>
  </table>

