          {{ frappe.utils.fmt_money(doc.vat) }}
        </td>
      </tr>
      <tr class="grand-total">
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"></td>
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>Grand Total</strong></td>
        <td class="rt" style="white-space: nowrap; border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>{{ frappe.utils.fmt_money(doc.grand_total) }}</strong></td>
      </tr>
    </table>
