html:
{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}

<div class="main-container">
  {{ add_header(0, 1, doc, letter_head, no_letterhead, print_settings) }}

  {%- set heading_label = (
      "Proforma Invoice" if doc.docstatus == 0 else
      "Sales Invoice"
  ) -%}

  {%- set cust_addr = frappe.get_all("Address", filters={"link_name": doc.customer_name, "link_doctype": "Customer"}, fields=["address_line1", "address_line2", "city", "country"], limit=1) -%}

  <style>
  {# Border inset inside the printable area (0 = touch printable edge) #}
  {%- set border_inset = 0 -%}
  {# Inner padding between border and content #}
  {%- set content_inner_padding = 3 -%}

  html, body { margin:0; padding:0; height:100%; font-family: Arial, sans-serif; font-size: 13px; }

  .print-format {
    position:relative;
    background:transparent !important;
    margin:0 !important;
    padding:0;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .page-border {
    position:fixed;
    top:{{ border_inset }}mm;
    left:{{ border_inset }}mm;
    right:{{ border_inset }}mm;
    bottom:{{ border_inset }}mm;
    border:1px solid #000;
    z-index:0;
    pointer-events:none;
  }

  .main-container {
    position:relative;
    z-index:1;
    padding:{{ content_inner_padding }}mm;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .print-heading,
  .print-heading + div { display:none !important; }

  .heading-label {
    font-size:18px;
    font-weight:600;
    text-align:center;
    color:#222;
    margin-bottom:8px;
    letter-spacing:1px;
    text-transform:uppercase;
    font-family: Arial, sans-serif;
  }

  .section { margin-bottom:8px; }

  .section-title {
    font-size:13px;
    font-weight:600;
    border-bottom:1px solid #000;
    color:#000;
    margin-bottom:4px;
    padding-bottom:2px;
    font-family: Arial, sans-serif;
  }

  .letter-head {
    margin-bottom:2mm !important;
  }

  .section:first-of-type {
    margin-top:0;
  }

  .info-table, .subscription-table, .items-table, .summary-table { width:100%; border-collapse:collapse; }
  .info-table td { 
    padding:2px 0; 
    vertical-align:top; 
    font-size: 13px;
    font-family: Arial, sans-serif;
  }

  .subscription-table td {
    padding:4px 6px;
    border:1px solid #dde2e7;
    font-size:13px;
    line-height:1.3;
    vertical-align:top;
  }

  .items-table th {
    background:#fff;
    color:#000;
    border-bottom:1px solid #000;
    padding:4px 4px;
    font-weight:700;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .items-table td {
    padding:4px 4px;
    border-bottom:none;
    vertical-align:top;
    line-height:1.3;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .summary-table td {
    padding:4px 4px;
    border-bottom:1px solid #f0f3f5;
    line-height:1.2;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .rt { text-align:right; }
  .cn { text-align:center; }
  .grand-total td { 
    border-top: 2px solid #000; 
    border-bottom: 2px solid #000; 
    font-weight:700; 
  }

  .footer-note {
    font-size:13px;
    color:#000;
    text-align:center;
    margin-top:12px;
    font-family: Arial, sans-serif;
  }

  .item-name { color:#000; font-weight:500; }
  .item-desc { color:#000; }
  .item-description { white-space:normal; word-wrap:break-word; line-height:1.2; }

  .section:last-of-type { margin-bottom:8px; }

  .subscription-badge {
    display:inline-block;
    padding:2px 8px;
    background:#f0f9ff;
    border:1px solid #0284c7;
    color:#0284c7;
    font-size:11px;
    font-weight:600;
    border-radius:3px;
    margin-left:8px;
  }
  </style>

  <!-- Add border element -->
  <div class="page-border"></div>

  <div class="heading-label">{{ heading_label }}</div>

  <!-- Customer Information -->
  <div class="section">
    <table class="info-table">
      <tr>
        <td style="width: 50%;">
          <strong>To:</strong><br>
          <strong>{{ doc.customer_name }}</strong><br>
          {% if cust_addr %}
            {{ cust_addr[0].address_line1 or "" }}<br>
            {% if cust_addr[0].address_line2 %}{{ cust_addr[0].address_line2 }}<br>{% endif %}
            {{ cust_addr[0].city or "" }}, {{ cust_addr[0].country or "" }}<br>
          {% endif %}
          Tax ID No: {{ frappe.db.get_value("Customer", doc.customer, "tax_id") or "-" }}
        </td>
        <td class="rt" style="width: 50%;">
          Invoice No.: <strong>{{ doc.name }}</strong><br>
          Invoice Date: {{ frappe.utils.formatdate(doc.posting_date, "dd-MMM-yy") }}<br>
          Due Date: {{ frappe.utils.formatdate(doc.due_date, "dd-MMM-yy") }}<br>
          Currency: {{ doc.currency }}<br>
          Status: {{ doc.status }}<br>
          Outstanding: {{ frappe.utils.fmt_money(doc.outstanding_amount, currency=doc.currency, format="#,##0.00") }}
        </td>
      </tr>
    </table>
  </div>

  <!-- Subscription Details -->
  <div class="section">
    <div class="section-title">Subscription Service Details</div>
    <table class="subscription-table">
      <tr>
        <td style="width: 50%;">
          <strong>Subscription Plan:</strong><br>
          {{ doc.subscription or "FreightMas Cloud Hosting" }}
        </td>
        <td style="width: 50%;">
          <strong>Billing Period:</strong><br>
          {{ frappe.utils.formatdate(doc.from_date, "dd-MMM-yy") }} to 
          {{ frappe.utils.formatdate(doc.to_date, "dd-MMM-yy") }}
        </td>
      </tr>
    </table>
  </div>

  <!-- Invoice Items -->
  <div class="section">
    <div class="section-title">Invoice Items</div>
    <table class="items-table">
      <thead>
        <tr>
          <th class="cn" style="width:5%">#</th>
          <th style="width:50%">Description</th>
          <th class="cn" style="width:10%">QTY</th>
          <th class="rt" style="width:13%">Rate</th>
          <th class="rt" style="width:13%">Total</th>
        </tr>
      </thead>
      <tbody>
        {% for item in doc.items %}
        <tr>
          <td class="cn">{{ item.idx }}</td>
          <td class="item-description">
            <span class="item-name">{{ item.item_name }}</span>
            {% if item.get("custom_is_subscription") %}
              <span class="subscription-badge">SUBSCRIPTION</span>
            {% endif %}
            {% if item.description %}<br><span class="item-desc">{{ item.description|striptags|replace('\n', ' ')|replace('\r', ' ') }}</span>{% endif %}
          </td>
          <td class="cn">{{ item.qty or 1 }}</td>
          <td class="rt">{{ "{:,.2f}".format(item.rate or 0) }}</td>
          <td class="rt">{{ "{:,.2f}".format(item.amount) }}</td>
        </tr>
        {% endfor %}
      </tbody>
    </table>
  </div>

  <!-- Summary -->
  <div class="section">
    <div class="section-title">Summary</div>
    <table class="summary-table">
      <tr>
        <td style="width: 65%;">
          {% if doc.payment_terms_template %}
            <strong>Payment Terms:</strong> {{ doc.payment_terms_template }}
          {% endif %}
        </td>
        <td style="width: 20%;"><strong>Sub Total</strong></td>
        <td class="rt" style="width: 15%; white-space: nowrap;">{{ frappe.utils.fmt_money(doc.total, currency=doc.currency, format="#,##0.00") }}</td>
      </tr>
      <tr>
        <td style="border-bottom: none;"></td>
        <td style="border-bottom: none;"><strong>Discount</strong></td>
        <td class="rt" style="white-space: nowrap; border-bottom: none;">
          {{ frappe.utils.fmt_money(doc.discount_amount or 0, currency=doc.currency, format="#,##0.00") }}
        </td>
      </tr>
      <tr class="grand-total">
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"></td>
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>Grand Total</strong></td>
        <td class="rt" style="white-space: nowrap; border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>{{ frappe.utils.fmt_money(doc.grand_total, currency=doc.currency, format="#,##0.00") }}</strong></td>
      </tr>
    </table>
  </div>

  <!-- Amount in Words -->
  {% if doc.in_words %}
  <p style="margin-top: 15px;"><strong>Amount in Words:</strong> {{ doc.in_words }}</p>
  {% endif %}

  <!-- Banking Details -->
  {% if doc.banking_profile %}
  {% set banking_details = frappe.get_doc("Banking Profile", doc.banking_profile) %}
  <div class="section">
    <div class="section-title">Banking Details</div>
    <table class="info-table">
      <tr>
        <td style="width: 50%;">
          Bank Name: {{ banking_details.bank_name }}<br>
          Account Name: {{ banking_details.account_name }}<br>
          Account Number: {{ banking_details.account_number }}
        </td>
        <td style="width: 50%;">
          {% if banking_details.branch %}Branch: {{ banking_details.branch }}<br>{% endif %}
          {% if banking_details.swift_code %}Swift Code: {{ banking_details.swift_code }}<br>{% endif %}
          Currency: {{ banking_details.currency }}
        </td>
      </tr>
    </table>
    <hr style="border: none; border-top: 1px solid #000; margin-top: 8px; margin-bottom: 8px;">
  </div>
  {% endif %}

  <!-- Terms and Conditions -->
  {% if doc.tc_name %}
    {% set terms_doc = frappe.get_doc("Terms and Conditions", doc.tc_name) %}
    <div class="section">
      <div class="section-title">Terms & Conditions</div>
      <table class="info-table">
        <tr>
          <td style="font-size: 10px; line-height: 1.3; text-align: justify;">{{ terms_doc.terms|striptags|replace('\n', ' ')|replace('\r', ' ') }}</td>
        </tr>
      </table>
      <hr style="border: none; border-top: 1px solid #000; margin-top: 8px; margin-bottom: 8px;">
    </div>
  {% endif %}

  <div class="footer-note">
    Thank you for your business! For support, please contact support@freightmas.co.zw
  </div>
</div>
