html:
{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}
{{ add_header(0, 1, doc, letter_head, no_letterhead, print_settings) }}

{%- set heading_label = (
    "Credit Note" if doc.is_return == 1 and doc.docstatus == 1 else
    "Draft Credit Note" if doc.is_return == 1 and doc.docstatus == 0 else
    "Proforma Invoice" if doc.docstatus == 0 else
    "Tax Invoice" if doc.docstatus == 1 else
    "Invoice"
) -%}

{%- set cust_addr = frappe.get_all("Address", filters={"link_name": doc.customer, "link_doctype": "Customer"}, fields=["address_line1", "address_line2", "city", "country"], limit=1) -%}

<style>
body {
  font-family: "Inter", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
  font-size: 11px;
  color: #222;
  margin: 10px;
  line-height: 1.4;
}

.print-heading,
.print-heading + div {
  display: none !important;
}

.heading-label {
  font-size: 18px;
  font-weight: 600;
  text-align: center;
  color: #222;
  margin-bottom: 20px;
  letter-spacing: 1px;
  text-transform: uppercase;
}

.section {
  margin-bottom: 10px;  /* Reduced from 15px to 10px */
}

.section-title {
  font-size: 13px;
  font-weight: 600;
  border-bottom: 2px solid #3f51b5;  /* Updated to corporate blue */
  color: #3f51b5;  /* Updated to corporate blue */
  margin-bottom: 5px;
  padding-bottom: 2px;
}

.info-table, .job-table, .items-table, .summary-table {
  width: 100%;
  border-collapse: collapse;
}

.info-table td {
  padding: 4px 0;
  vertical-align: top;
}

.job-table td {
  padding: 2px 2px;  /* Reduced horizontal padding from 5px to 4px */
  border: 1px solid #dde2e7;
  font-size: 12px;
  line-height: 1.2;  /* Increased from 1.0 to 1.3 for better readability */
  vertical-align: top;
}

.items-table th {
  background-color: #fff;
  color: #222;  /* Dark text for readability */
  border-bottom: 2px solid #3f51b5;  /* Only bottom border */
  padding: 4px 6px;
  font-weight: 700;  /* Bolder text for emphasis */
  font-size: 12px;  /* Increased from 11px to 12px */
}

.items-table td {
  padding: 3px 6px;  /* Reduced from 6px to 3px top/bottom */
  border-bottom: 1px solid #f0f3f5;
  vertical-align: top;
  line-height: 1.2;  /* Added to control line spacing */
}

.summary-table td {
  padding: 3px 6px;  /* Reduced from 6px to 3px top/bottom */
  border-bottom: 1px solid #f0f3f5;
  line-height: 1.1;  /* Added tight line spacing */
}

.rt { text-align: right; }
.cn { text-align: center; }

.grand-total {
  background-color: #e8eaf6;  /* Light version of corporate blue */
  font-weight: 700;
}

.footer-note {
  font-size: 11px;  /* Changed from 10px to 11px */
  color: #666;
  text-align: center;
  margin-top: 12px;
}

/* Item description styling to match your current format */
.item-name {
  color: #222;
  font-weight: 500;
}

.item-desc {
  color: #222;  /* Changed from #666 to #222 to match item-name */
  font-weight: normal;
}

/* Ensure no extra line breaks in descriptions */
.item-description {
  white-space: normal;
  word-wrap: break-word;
  line-height: 1.0;  /* Added tighter line spacing */
}

/* Add this specific rule for the summary section */
.section:last-of-type {
  margin-bottom: 5px;  /* Reduced spacing for summary section */
}
</style>

<div class="heading-label">{{ heading_label }}</div>

<!-- Customer Information -->
<div class="section">
  <table class="info-table">
    <tr>
      <td style="width: 50%;">
        <strong>To:</strong><br>
        <strong>{{ doc.customer_name }}</strong><br>
        {% if cust_addr %}
          {{ cust_addr[0].address_line1 or "" }}<br>
          {% if cust_addr[0].address_line2 %}{{ cust_addr[0].address_line2 }}<br>{% endif %}
          {{ cust_addr[0].city or "" }}, {{ cust_addr[0].country or "" }}<br>
        {% endif %}
        Tax ID No: {{ frappe.db.get_value("Customer", doc.customer, "tax_id") or "-" }}
      </td>
      <td class="rt" style="width: 50%;">
        <strong>Invoice No.:</strong> <strong>{{ doc.name }}</strong><br>
        <strong>Posting Date:</strong> {{ frappe.utils.formatdate(doc.posting_date, "dd-MMM-yy") }}<br>
        <strong>Due Date:</strong> {{ frappe.utils.formatdate(doc.due_date, "dd-MMM-yy") }}<br>
        <strong>Currency:</strong> {{ doc.currency }}<br>
        <strong>Status:</strong> {{ doc.status }}<br>
        <strong>Outstanding:</strong> {{ frappe.utils.fmt_money(doc.outstanding_amount, currency=doc.currency, format="#,##0.00") }}
      </td>
    </tr>
  </table>
</div>

<!-- Dynamic Job Details Section -->
{% if doc.is_clearing_invoice and doc.clearing_job_reference %}
  {% set job = frappe.get_doc("Clearing Job", doc.clearing_job_reference) %}
  <div class="section">
    <div class="section-title">Clearing Job Details</div>
    <table class="job-table">
      <tr>
        <td style="width: 33.33%;"><strong>Consignee:</strong> {{ job.consignee or "-" }}</td>
        <td style="width: 33.33%;"><strong>Job Reference:</strong> {{ job.name }}</td>
        <td style="width: 33.33%;"><strong>Client Reference:</strong> {{ job.customer_reference or "-" }}</td>
      </tr>
      <tr>
        <td><strong>Origin/Dest:</strong> {{ job.origin or "-" }} → {{ job.destination or "-" }}</td>
        <td><strong>Job Type:</strong> {{ job.direction or "-" }}</td>
        <td><strong>BL Number:</strong> {{ job.bl_number or "-" }}</td>
      </tr>
      <tr>
        <td colspan="2"><strong>Cargo Desc.:</strong> {{ job.cargo_description or "-" }}</td>
        <td><strong>Cargo Count:</strong> {{ job.cargo_count or "-" }}</td>
      </tr>
    </table>
  </div>

{% elif doc.is_forwarding_invoice and doc.forwarding_job_reference %}
  {% set job = frappe.get_doc("Forwarding Job", doc.forwarding_job_reference) %}
  <div class="section">
    <div class="section-title">Forwarding Job Details</div>
    <table class="job-table">
      <tr>
        <td style="width: 33.33%;"><strong>Consignee:</strong> {{ job.consignee or "-" }}</td>
        <td style="width: 33.33%;"><strong>Job Reference:</strong> {{ job.name }}</td>
        <td style="width: 33.33%;"><strong>Client Reference:</strong> {{ job.customer_reference or "-" }}</td>
      </tr>
      <tr>
        <td><strong>Origin/Dest:</strong> {{ job.port_of_loading or "-" }} → {{ job.destination or "-" }}</td>
        <td><strong>Job Type:</strong> {{ (job.shipment_mode or "") ~ " " ~ (job.direction or "") }}</td>
        <td><strong>BL Number:</strong> {{ job.bl_number or "-" }}</td>
      </tr>
      <tr>
        <td colspan="2"><strong>Cargo Desc.:</strong> {{ job.cargo_description or "-" }}</td>
        <td><strong>Cargo Count:</strong> {{ job.cargo_count or "-" }}</td>
      </tr>
    </table>
  </div>

{% elif doc.is_road_freight_invoice and doc.road_freight_job_reference %}
  {% set job = frappe.get_doc("Road Freight Job", doc.road_freight_job_reference) %}
  <div class="section">
    <div class="section-title">Road Freight Job Details</div>
    <table class="job-table">
      <tr>
        <td style="width: 33.33%;"><strong>Consignee:</strong> {{ job.consignee or "-" }}</td>
        <td style="width: 33.33%;"><strong>Job Reference:</strong> {{ job.name }}</td>
        <td style="width: 33.33%;"><strong>Client Reference:</strong> {{ job.customer_reference or "-" }}</td>
      </tr>
      <tr>
        <td><strong>Origin/Dest:</strong> {{ job.port_of_loading or "-" }} → {{ job.port_of_discharge or "-" }}</td>
        <td><strong>Job Type:</strong> {{ job.direction or "-" }}</td>
        <td><strong>Shipment Type:</strong> {{ job.shipment_type or "-" }}</td>
      </tr>
      <tr>
        <td colspan="2"><strong>Cargo Desc.:</strong> {{ job.cargo_description or "-" }}</td>
        <td><strong>Trucks Confirmed:</strong> {{ job.trucks_confirmed or "-" }}</td>
      </tr>
    </table>
  </div>

{% elif doc.is_trip_invoice and doc.trip_reference %}
  {% set job = frappe.get_doc("Trip", doc.trip_reference) %}
  <div class="section">
    <div class="section-title">Trip Details</div>
    <table class="job-table">
      <tr>
        <td style="width: 33.33%;"><strong>Truck:</strong> {{ job.horse or job.trailer or "-" }}</td>
        <td style="width: 33.33%;"><strong>Trip Reference:</strong> {{ job.name }}</td>
        <td style="width: 33.33%;"><strong>Client Reference:</strong> {{ job.customer_reference or "-" }}</td>
      </tr>
      <tr>
        <td><strong>Origin/Dest:</strong> {{ job.route or "-" }}</td>
        <td><strong>Trip Type:</strong> {{ job.trip_direction or "-" }}</td>
        <td><strong>Cargo Type:</strong> {{ job.cargo_type or "-" }}</td>
      </tr>
      <tr>
        <td colspan="2"><strong>Cargo Desc.:</strong> {{ job.cargo_description or "-" }}</td>
        <td><strong>Container No:</strong> {{ job.container_number or "-" }}</td>
      </tr>
    </table>
  </div>

{% elif doc.forwarding_job_reference %}
  {% set job = frappe.get_doc("Forwarding Job", doc.forwarding_job_reference) %}
  <div class="section">
    <div class="section-title">Forwarding Job Details</div>
    <table class="job-table">
      <tr>
        <td style="width: 33.33%;"><strong>Consignee:</strong> {{ job.consignee or "-" }}</td>
        <td style="width: 33.33%;"><strong>Job Reference:</strong> {{ job.name }}</td>
        <td style="width: 33.33%;"><strong>Client Reference:</strong> {{ job.customer_reference or "-" }}</td>
      </tr>
      <tr>
        <td><strong>Origin/Dest:</strong> {{ job.port_of_loading or "-" }} → {{ job.destination or "-" }}</td>
        <td><strong>Job Type:</strong> {{ (job.shipment_mode or "") ~ " " ~ (job.direction or "") }}</td>
        <td><strong>BL Number:</strong> {{ job.bl_number or "-" }}</td>
      </tr>
      <tr>
        <td colspan="2"><strong>Cargo Desc.:</strong> {{ job.cargo_description or "-" }}</td>
        <td><strong>Cargo Count:</strong> {{ job.cargo_count or "-" }}</td>
      </tr>
    </table>
  </div>
{% endif %}

<!-- Invoice Items -->
<div class="section">
  <div class="section-title">Invoice Items</div>
  <table class="items-table">
    <thead>
      <tr>
        <th class="cn" style="width:5%">#</th>
        <th style="width:55%">Description</th>
        <th class="rt" style="width:13%">Amount</th>
        <th class="rt" style="width:13%">VAT</th>
        <th class="rt" style="width:14%">Total</th>
      </tr>
    </thead>
    <tbody>
      {% set vat_rate = frappe.db.get_value("Sales Taxes and Charges", {"parent": doc.name}, "rate") or 0 %}
      {% for item in doc.items %}
      <tr>
        <td class="cn">{{ item.idx }}</td>
        <td class="item-description">
          <span class="item-name">{{ item.item_name }}</span>{% if item.description %} - <span class="item-desc">{{ item.description|striptags|replace('\n', ' ')|replace('\r', ' ') }}</span>{% endif %}
        </td>
        <td class="rt">{{ "{:,.2f}".format(item.amount) }}</td>
        <td class="rt">{{ "{:,.2f}".format(item.amount * (vat_rate / 100)) }}</td>
        <td class="rt">{{ "{:,.2f}".format(item.amount * (1 + (vat_rate / 100))) }}</td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>

<!-- Summary -->
<div class="section">
  <div class="section-title">Summary</div>
  <table class="summary-table">
    <tr>
      <td style="width: 65%;">
        {% if doc.payment_terms_template %}
          <strong>Payment Terms:</strong> {{ doc.payment_terms_template }}
        {% endif %}
      </td>
      <td style="width: 20%;"><strong>Sub Total</strong></td>
      <td class="rt" style="width: 15%; white-space: nowrap;">{{ frappe.utils.fmt_money(doc.total, currency=doc.currency, format="#,##0.00") }}</td>
    </tr>
    <tr>
      <td></td>
      <td><strong>VAT ({{ "%.0f"|format(vat_rate) }}%)</strong></td>
      <td class="rt" style="white-space: nowrap;">
        {% if doc.total_taxes_and_charges %}
          {{ frappe.utils.fmt_money(doc.total_taxes_and_charges, currency=doc.currency, format="#,##0.00") }}
        {% else %}
          {{ frappe.utils.fmt_money(0, currency=doc.currency, format="#,##0.00") }}
        {% endif %}
      </td>
    </tr>
    <tr class="grand-total">
      <td></td>
      <td><strong>Grand Total</strong></td>
      <td class="rt" style="white-space: nowrap;"><strong>{{ frappe.utils.fmt_money(doc.grand_total, currency=doc.currency, format="#,##0.00") }}</strong></td>
    </tr>
  </table>
</div>

<!-- Amount in Words -->
{% if doc.in_words %}
<p style="margin-top: 15px;"><strong>Amount in Words:</strong> {{ doc.in_words }}</p>
{% endif %}

<!-- Banking Details -->
{% if doc.banking_profile %}
{% set banking_details = frappe.get_doc("Banking Profile", doc.banking_profile) %}
<div class="section">
  <div class="section-title">Banking Details</div>
  <table class="info-table">
    <tr>
      <td style="width: 33.33%;">
        <strong>Bank Name:</strong> {{ banking_details.bank_name }}<br>
        {% if banking_details.branch %}<strong>Branch:</strong> {{ banking_details.branch }}<br>{% endif %}
        <strong>Account Name:</strong> {{ banking_details.account_name }}
      </td>
      <td style="width: 33.33%;">
        <strong>Account Number:</strong> {{ banking_details.account_number }}<br>
        <strong>Currency:</strong> {{ banking_details.currency }}
      </td>
      <td style="width: 33.33%;">
        {% if banking_details.swift_code %}<strong>Swift Code:</strong> {{ banking_details.swift_code }}<br>{% endif %}
        {% if banking_details.notes %}<strong>Notes:</strong> {{ banking_details.notes }}{% endif %}
      </td>
    </tr>
  </table>
</div>
{% endif %}

<div class="footer-note">
  Thank you for your business! If you have any questions, please contact us.
</div>
