html:
{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}

<div class="main-container">
  {{ add_header(0, 1, doc, letter_head, no_letterhead, print_settings) }}

  {%- set heading_label = (
      "FREIGHT QUOTATION" if doc.is_freight_quote else
      "QUOTATION"
  ) -%}

  {%- set cust_addr = frappe.get_all("Address", filters={"link_name": doc.customer_name, "link_doctype": "Customer"}, fields=["address_line1", "address_line2", "city", "country"], limit=1) -%}

  <style>
  {# Border inset inside the printable area (0 = touch printable edge) #}
  {%- set border_inset = 0 -%}
  {# Inner padding between border and content #}
  {%- set content_inner_padding = 3 -%}

  html, body { margin:0; padding:0; height:100%; font-family: Arial, sans-serif; font-size: 13px; }

  /* Prevent logo stretching in PDF output */
  .letter-head img,
  .letter-head .logo,
  .letter-head .logo img,
  .header-logo img,
  .company-logo img {
    max-width: 160px !important;    /* adjust as needed */
    max-height: 90px !important;    /* adjust as needed */
    width: auto !important;
    height: auto !important;
    object-fit: contain !important;
    display: inline-block !important;
  }

  .print-format {
    position:relative;
    background:transparent !important;
    margin:0 !important;
    padding:0;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .page-border {
    position:fixed;
    top:{{ border_inset }}mm;
    left:{{ border_inset }}mm;
    right:{{ border_inset }}mm;
    bottom:{{ border_inset }}mm;
    border:1px solid #000;
    z-index:0;
    pointer-events:none;
  }

  .main-container {
    position:relative;
    z-index:1;
    padding:{{ content_inner_padding }}mm;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .print-heading,
  .print-heading + div { display:none !important; }

  .heading-label {
    font-size:18px;
    font-weight:600;
    text-align:center;
    color:#222;
    margin-bottom:1px;
    letter-spacing:1px;
    text-transform:uppercase;
    font-family: Arial, sans-serif;
  }

  .section { margin-bottom:1px; }

  .section-title {
    font-size:13px;
    font-weight:600;
    border-bottom:1px solid #000;
    color:#000;
    margin-bottom:2px;
    padding-bottom:2px;
    font-family: Arial, sans-serif;
  }

  .letter-head {
    margin-bottom:2mm !important;
  }

  .section:first-of-type {
    margin-top:0;
  }

  .info-table, .job-table, .items-table, .summary-table { width:100%; border-collapse:collapse; }
  .info-table td { 
    padding:2px 0; 
    vertical-align:top; 
    font-size: 13px;
    font-family: Arial, sans-serif;
  }

  .job-table td {
    padding:2px 2px;
    border:1px solid #dde2e7;
    font-size:13px;
    line-height:1.1;
    vertical-align:top;
  }

  .items-table th {
    background:#fff;
    color:#000;
    border-bottom:1px solid #000;
    padding:3px 4px;
    font-weight:700;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .items-table td {
    padding:3px 4px;
    border-bottom:none;
    vertical-align:top;
    line-height:1.15;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .summary-table td {
    padding:3px 4px;
    border-bottom:1px solid #f0f3f5;
    line-height:1.1;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .rt { text-align:right; }
  .cn { text-align:center; }
  .grand-total td { 
    border-top: 2px solid #000; 
    border-bottom: 2px solid #000; 
    font-weight:700; 
  }

  .footer-note {
    font-size:13px;
    color:#000;
    text-align:center;
    margin-top:8px;
    font-family: Arial, sans-serif;
  }

  .item-name { color:#000; font-weight:500; }
  .item-desc { color:#000; }
  .item-description { white-space:normal; word-wrap:break-word; line-height:1.05; }

  .section:last-of-type { margin-bottom:4px; }
  </style>

  <!-- Add border element -->
  <div class="page-border"></div>

  <div class="heading-label">{{ heading_label }}</div>

  <!-- Customer Information -->
  <div class="section">
    <table class="info-table">
      <tr>
        <td style="width: 50%;">
          <strong>To:</strong><br>
          <strong>{{ doc.customer_name }}</strong><br>
          {% if cust_addr %}
            {{ cust_addr[0].address_line1 or "" }}<br>
            {% if cust_addr[0].address_line2 %}{{ cust_addr[0].address_line2 }}<br>{% endif %}
            {{ cust_addr[0].city or "" }}, {{ cust_addr[0].country or "" }}<br>
          {% endif %}
        </td>
        <td class="rt" style="width: 50%;">
          Quotation No.: <strong>{{ doc.name }}</strong><br>
          Date: {{ frappe.utils.formatdate(doc.transaction_date, "dd-MMM-yy") }}<br>
          Valid Until: {{ frappe.utils.formatdate(doc.valid_till, "dd-MMM-yy") }}<br>
          Currency: {{ doc.currency }}<br>
          Status: {{ doc.status }}
        </td>
      </tr>
    </table>
  </div>

  <!-- Freight Job Info -->
  {% if doc.is_freight_quote %}
  <div class="section">
    <div class="section-title">Freight Service Details</div>
    <table class="job-table">
      <tr>
        <td>Job Type: {{ doc.job_type or "-" }}</td>
        <td>Origin: {{ doc.origin_port or "-" }}</td>
        <td>Destination: {{ doc.destination_port or "-" }}</td>
      </tr>
      <tr>
        <td colspan="3">Description: {{ doc.job_description or "-" }}</td>
      </tr>
    </table>
  </div>
  {% endif %}

  <!-- Quotation Items -->
  <div class="section">
    <div class="section-title">Quotation Items</div>
    <table class="items-table">
      <thead>
        <tr>
          <th class="cn" style="width:5%">#</th>
          <th style="width:45%">Description</th>
          <th class="cn" style="width:10%">QTY</th>
          <th class="rt" style="width:15%">Rate</th>
          <th class="rt" style="width:15%">Total</th>
        </tr>
      </thead>
      <tbody>
        {% set vat_rate = frappe.db.get_value("Sales Taxes and Charges", {"parent": doc.name}, "rate") or 0 %}

        {% if doc.is_freight_quote and doc.bundle_charges %}
        {# Bundle charges: Show single consolidated service line for freight quotes only #}
        {% set bundle_description = doc.job_type + " Service" %}
        <tr>
          <td class="cn">1</td>
          <td class="item-description">
            <span class="item-name">{{ bundle_description }}</span>
          </td>
          <td class="cn">1</td>
          <td class="rt">{{ "{:,.2f}".format(doc.total or 0) }}</td>
          <td class="rt">{{ "{:,.2f}".format(doc.total or 0) }}</td>
        </tr>
        {% else %}
        {# Regular detailed line items #}
        {% for item in doc.items %}
        <tr>
          <td class="cn">{{ item.idx }}</td>
          <td class="item-description">
            <span class="item-name">{{ item.item_name }}</span>{% if item.description %} - <span class="item-desc">{{ item.description|striptags|replace('\n', ' ')|replace('\r', ' ') }}</span>{% endif %}
          </td>
          <td class="cn">{{ item.qty or 1 }}</td>
          <td class="rt">{{ "{:,.2f}".format(item.rate or 0) }}</td>
          <td class="rt">{{ "{:,.2f}".format(item.amount) }}</td>
        </tr>
        {% endfor %}
        {% endif %}
      </tbody>
    </table>
  </div>

  <!-- Summary -->
  <div class="section">
    <div class="section-title">Summary</div>
    <table class="summary-table">
      <tr>
        <td style="width: 65%;">
          {% if doc.payment_terms_template %}
            <strong>Payment Terms:</strong> {{ doc.payment_terms_template }}
          {% endif %}
        </td>
        <td style="width: 20%;"><strong>Sub Total</strong></td>
        <td class="rt" style="width: 15%; white-space: nowrap;">{{ frappe.utils.fmt_money(doc.total, currency=doc.currency, format="#,##0.00") }}</td>
      </tr>
      <tr>
        <td style="border-bottom: none;"></td>
        <td style="border-bottom: none;"><strong>VAT ({{ "%.0f"|format(vat_rate) }}%)</strong></td>
        <td class="rt" style="white-space: nowrap; border-bottom: none;">
          {% if doc.total_taxes_and_charges %}
            {{ frappe.utils.fmt_money(doc.total_taxes_and_charges, currency=doc.currency, format="#,##0.00") }}
          {% else %}
            {{ frappe.utils.fmt_money(0, currency=doc.currency, format="#,##0.00") }}
          {% endif %}
        </td>
      </tr>
      <tr class="grand-total">
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"></td>
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>Grand Total</strong></td>
        <td class="rt" style="white-space: nowrap; border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>{{ frappe.utils.fmt_money(doc.grand_total, currency=doc.currency, format="#,##0.00") }}</strong></td>
      </tr>
    </table>
  </div>

  <!-- Amount in Words -->
  {% if doc.in_words %}
  <p style="margin-top: 15px;"><strong>Amount in Words:</strong> {{ doc.in_words }}</p>
  {% endif %}

  <!-- Terms and Conditions -->
  {% if doc.tc_name %}
    {% set terms_doc = frappe.get_doc("Terms and Conditions", doc.tc_name) %}
    <div class="section">
      <div class="section-title">Terms & Conditions</div>
      <table class="info-table">
        <tr>
          <td style="font-size: 10px; line-height: 1.3; text-align: justify;">{{ terms_doc.terms|striptags|replace('\n', ' ')|replace('\r', ' ') }}</td>
        </tr>
      </table>
      <hr style="border: none; border-top: 1px solid #000; margin-top: 8px; margin-bottom: 8px;">
    </div>
  {% endif %}

  <div class="footer-note">
    Thank you for the opportunity to quote. Please reach out if you have any questions.
  </div>
</div>
