html:
{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}
{{ add_header(0, 1, doc, letter_head, no_letterhead, print_settings) }}

{%- set heading_label = (
    "Freight Quotation" if doc.is_freight_quote else
    "Quotation"
) -%}

{%- set cust_addr = frappe.get_all("Address", filters={"link_name": doc.customer_name, "link_doctype": "Customer"}, fields=["address_line1", "address_line2", "city", "country"], limit=1) -%}

<style>
body {
  font-family: "Inter", -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;
  font-size: 11px;
  color: #222;
  margin: 10px;
  line-height: 1.4;
}

.print-heading,
.print-heading + div {
  display: none !important;
}

.heading-label {
  font-size: 18px;
  font-weight: 600;
  text-align: center;
  color: #222;
  margin-bottom: 20px;
  letter-spacing: 1px;
  text-transform: uppercase;
}

.section {
  margin-bottom: 10px;
}

.section-title {
  font-size: 13px;
  font-weight: 600;
  border-bottom: 2px solid #3f51b5;
  color: #3f51b5;
  margin-bottom: 5px;
  padding-bottom: 2px;
}

.info-table, .job-table, .items-table, .summary-table {
  width: 100%;
  border-collapse: collapse;
}

.info-table td {
  padding: 4px 0;
  vertical-align: top;
}

.job-table td {
  padding: 2px 2px;
  border: 1px solid #dde2e7;
  font-size: 12px;
  line-height: 1.2;
  vertical-align: top;
}

.items-table th {
  background-color: #fff;
  color: #222;
  border-bottom: 2px solid #3f51b5;
  padding: 4px 6px;
  font-weight: 700;
  font-size: 12px;
}

.items-table td {
  padding: 3px 6px;
  border-bottom: 1px solid #f0f3f5;
  vertical-align: top;
  line-height: 1.2;
}

.summary-table td {
  padding: 3px 6px;
  border-bottom: 1px solid #f0f3f5;
  line-height: 1.1;
}

.rt { text-align: right; }
.cn { text-align: center; }

.grand-total {
  background-color: #e8eaf6;
  font-weight: 700;
}

.footer-note {
  font-size: 11px;
  color: #666;
  text-align: center;
  margin-top: 12px;
}

.item-name {
  color: #222;
  font-weight: 500;
}

.item-desc {
  color: #222;
  font-weight: normal;
}

.item-description {
  white-space: normal;
  word-wrap: break-word;
  line-height: 1.0;
}

.section:last-of-type {
  margin-bottom: 5px;
}

.terms-conditions {
  font-size: 8px;
  color: #555;
  margin-top: 10px;
  line-height: 1.2;
  border-top: 1px solid #ddd;
  padding-top: 8px;
}

.terms-title {
  font-weight: 600;
  margin-bottom: 4px;
  font-size: 9px;
}
</style>

<div class="heading-label">{{ heading_label }}</div>

<!-- Customer Information -->
<div class="section">
  <table class="info-table">
    <tr>
      <td style="width: 50%;">
        <strong>To:</strong><br>
        <strong>{{ doc.customer_name }}</strong><br>
        {% if cust_addr %}
          {{ cust_addr[0].address_line1 or "" }}<br>
          {% if cust_addr[0].address_line2 %}{{ cust_addr[0].address_line2 }}<br>{% endif %}
          {{ cust_addr[0].city or "" }}, {{ cust_addr[0].country or "" }}<br>
        {% endif %}
      </td>
      <td class="rt" style="width: 50%;">
        <strong>Quotation No.:</strong> <strong>{{ doc.name }}</strong><br>
        <strong>Date:</strong> {{ frappe.utils.formatdate(doc.transaction_date, "dd-MMM-yy") }}<br>
        <strong>Valid Until:</strong> {{ frappe.utils.formatdate(doc.valid_till, "dd-MMM-yy") }}<br>
        <strong>Currency:</strong> {{ doc.currency }}<br>
        <strong>Status:</strong> {{ doc.status }}
      </td>
    </tr>
  </table>
</div>

<!-- Freight Job Info -->
{% if doc.is_freight_quote %}
<div class="section">
  <div class="section-title">Freight Service Details</div>
  <table class="job-table">
    <tr>
      <td><strong>Job Type:</strong> {{ doc.job_type or "-" }}</td>
      <td><strong>Origin:</strong> {{ doc.origin or "-" }}</td>
      <td><strong>Destination:</strong> {{ doc.destination or "-" }}</td>
    </tr>
    <tr>
      <td colspan="3"><strong>Description:</strong> {{ doc.job_description or "-" }}</td>
    </tr>
  </table>
</div>
{% endif %}

<!-- Quotation Items -->
<div class="section">
  <div class="section-title">Quotation Items</div>
  <table class="items-table">
    <thead>
      <tr>
        <th class="cn" style="width:5%">#</th>
        <th style="width:45%">Description</th>
        <th class="cn" style="width:10%">QTY</th>
        <th class="rt" style="width:15%">Rate</th>
        <th class="rt" style="width:15%">Total</th>
      </tr>
    </thead>
    <tbody>
      {% set vat_rate = frappe.db.get_value("Sales Taxes and Charges", {"parent": doc.name}, "rate") or 0 %}
      {% for item in doc.items %}
      <tr>
        <td class="cn">{{ item.idx }}</td>
        <td class="item-description">
          <span class="item-name">{{ item.item_name }}</span>{% if item.description %} - <span class="item-desc">{{ item.description|striptags|replace('\n', ' ')|replace('\r', ' ') }}</span>{% endif %}
        </td>
        <td class="cn">{{ item.qty or 1 }}</td>
        <td class="rt">{{ "{:,.2f}".format(item.rate or 0) }}</td>
        <td class="rt">{{ "{:,.2f}".format(item.amount) }}</td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>

<!-- Summary -->
<div class="section">
  <div class="section-title">Summary</div>
  <table class="summary-table">
    <tr>
      <td style="width: 65%;">
        {% if doc.payment_terms_template %}
          <strong>Payment Terms:</strong> {{ doc.payment_terms_template }}
        {% endif %}
      </td>
      <td style="width: 20%;"><strong>Sub Total</strong></td>
      <td class="rt" style="width: 15%; white-space: nowrap;">{{ frappe.utils.fmt_money(doc.total, currency=doc.currency, format="#,##0.00") }}</td>
    </tr>
    <tr>
      <td></td>
      <td><strong>VAT ({{ "%.0f"|format(vat_rate) }}%)</strong></td>
      <td class="rt" style="white-space: nowrap;">
        {% if doc.total_taxes_and_charges %}
          {{ frappe.utils.fmt_money(doc.total_taxes_and_charges, currency=doc.currency, format="#,##0.00") }}
        {% else %}
          {{ frappe.utils.fmt_money(0, currency=doc.currency, format="#,##0.00") }}
        {% endif %}
      </td>
    </tr>
    <tr class="grand-total">
      <td></td>
      <td><strong>Grand Total</strong></td>
      <td class="rt" style="white-space: nowrap;"><strong>{{ frappe.utils.fmt_money(doc.grand_total, currency=doc.currency, format="#,##0.00") }}</strong></td>
    </tr>
  </table>
</div>

{% if doc.in_words %}
<p style="margin-top: 15px;"><strong>Amount in Words:</strong> {{ doc.in_words }}</p>
{% endif %}

<!-- Terms and Conditions -->
{% if doc.tc_name %}
  {% set terms_doc = frappe.get_doc("Terms and Conditions", doc.tc_name) %}
  <div class="terms-conditions">
    <div class="terms-title">{{ terms_doc.title or "TERMS AND CONDITIONS" }}</div>
    {{ terms_doc.terms|striptags|replace('\n', ' ')|replace('\r', ' ') }}
  </div>
{% endif %}

<div class="footer-note">
  Thank you for the opportunity to quote. Please reach out if you have any questions.
</div>
