html:
{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}

<div class="main-container">
  {{ add_header(0, 1, doc, letter_head, no_letterhead, print_settings) }}

  {%- set heading_label = (
      "FREIGHT QUOTATION" if doc.is_freight_quote else
      "QUOTATION"
  ) -%}

  {%- set cust_addr = frappe.get_all(
      "Address",
      filters={
        "link_name": doc.customer_name,
        "link_doctype": "Customer"
      },
      fields=["address_line1", "address_line2", "city", "country"],
      limit=1
  ) -%}

  <style>
  {%- set border_inset = 0 -%}
  {%- set content_inner_padding = 3 -%}

  html, body {
    margin:0;
    padding:0;
    height:100%;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .letter-head img,
  .letter-head .logo img,
  .company-logo img {
    max-width: 160px !important;
    max-height: 90px !important;
    width: auto !important;
    height: auto !important;
    object-fit: contain !important;
  }

  .print-format {
    margin:0 !important;
    padding:0;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .page-border {
    position:fixed;
    top:{{ border_inset }}mm;
    left:{{ border_inset }}mm;
    right:{{ border_inset }}mm;
    bottom:{{ border_inset }}mm;
    border:1px solid #000;
    pointer-events:none;
  }

  .main-container {
    position:relative;
    padding:{{ content_inner_padding }}mm;
  }

  .print-heading,
  .print-heading + div { display:none !important; }

  .heading-label {
    font-size:18px;
    font-weight:600;
    text-align:center;
    margin-bottom:2mm;
    letter-spacing:1px;
    text-transform:uppercase;
  }

  .section { margin-bottom:2mm; }

  .section-title {
    font-size:13px;
    font-weight:600;
    border-bottom:1px solid #000;
    margin-bottom:2px;
    padding-bottom:2px;
  }

  table { width:100%; border-collapse:collapse; }

  .info-table td {
    padding:2px 0;
    vertical-align:top;
  }

  .job-table td {
    padding:2px;
    border:1px solid #dde2e7;
  }

  .items-table th {
    border-bottom:1px solid #000;
    padding:3px 4px;
    font-weight:700;
  }

  .items-table td {
    padding:3px 4px;
    line-height:1.15;
  }

  .summary-table td {
    padding:3px 4px;
    border-bottom:1px solid #f0f3f5;
  }

  .rt { text-align:right; }
  .cn { text-align:center; }

  .grand-total td {
    border-top:2px solid #000;
    border-bottom:2px solid #000;
    font-weight:700;
  }

  .footer-note {
    text-align:center;
    margin-top:8px;
  }
  </style>

  <div class="page-border"></div>

  <div class="heading-label">{{ heading_label }}</div>

  <!-- Customer Info -->
  <div class="section">
    <table class="info-table">
      <tr>
        <td style="width:50%;">
          <strong>To:</strong><br>
          <strong>{{ doc.customer_name }}</strong><br>
          {% if cust_addr %}
            {{ cust_addr[0].address_line1 or "" }}<br>
            {% if cust_addr[0].address_line2 %}
              {{ cust_addr[0].address_line2 }}<br>
            {% endif %}
            {{ cust_addr[0].city or "" }} {{ cust_addr[0].country or "" }}
          {% endif %}
        </td>
        <td class="rt" style="width:50%;">
          <strong>Quotation No:</strong> {{ doc.name }}<br>
          <strong>Date:</strong> {{ frappe.utils.formatdate(doc.transaction_date) }}<br>
          <strong>Valid Until:</strong> {{ frappe.utils.formatdate(doc.valid_till) }}<br>
          <strong>Currency:</strong> {{ doc.currency }}<br>
          <strong>Status:</strong> {{ doc.status }}
        </td>
      </tr>
    </table>
  </div>

  <!-- Freight Details -->
  {% if doc.is_freight_quote %}
  <div class="section">
    <div class="section-title">Freight Service Details</div>
    <table class="job-table">
      <tr>
        <td>Job Type: {{ doc.job_type or "-" }}</td>
        <td>Origin: {{ doc.origin_port or "-" }}</td>
        <td>Destination: {{ doc.destination_port or "-" }}</td>
      </tr>
      <tr>
        <td colspan="3">Description: {{ doc.job_description or "-" }}</td>
      </tr>
    </table>
  </div>
  {% endif %}

  <!-- Items -->
  <div class="section">
    <div class="section-title">Quotation Items</div>
    <table class="items-table">
      <thead>
        <tr>
          <th class="cn" style="width:5%">#</th>
          <th>Description</th>
          <th class="cn" style="width:10%">Qty</th>
          <th class="rt" style="width:15%">Rate</th>
          <th class="rt" style="width:15%">Total</th>
        </tr>
      </thead>
      <tbody>
        {% if doc.is_freight_quote and doc.bundle_charges %}
          <tr>
            <td class="cn">1</td>
            <td>{{ doc.job_type }} Service</td>
            <td class="cn">1</td>
            <td class="rt">{{ frappe.utils.fmt_money(doc.total, currency=doc.currency) }}</td>
            <td class="rt">{{ frappe.utils.fmt_money(doc.total, currency=doc.currency) }}</td>
          </tr>
        {% else %}
          {% for item in doc.items %}
          <tr>
            <td class="cn">{{ item.idx }}</td>
            <td>{{ item.item_name }}{% if item.description %} - {{ item.description|striptags }}{% endif %}</td>
            <td class="cn">{{ item.qty }}</td>
            <td class="rt">{{ frappe.utils.fmt_money(item.rate, currency=doc.currency) }}</td>
            <td class="rt">{{ frappe.utils.fmt_money(item.amount, currency=doc.currency) }}</td>
          </tr>
          {% endfor %}
        {% endif %}
      </tbody>
    </table>
  </div>

  <!-- Summary -->
  <div class="section">
    <div class="section-title">Summary</div>
    <table class="summary-table">
      <tr>
        <td style="width:65%;">
          {% if doc.payment_terms_template %}
            <strong>Payment Terms:</strong> {{ doc.payment_terms_template }}
          {% endif %}
        </td>
        <td style="width:20%;"><strong>Sub Total</strong></td>
        <td class="rt" style="width:15%;">
          {{ frappe.utils.fmt_money(doc.total, currency=doc.currency) }}
        </td>
      </tr>
      <tr>
        <td></td>
        <td><strong>VAT</strong></td>
        <td class="rt">
          {{ frappe.utils.fmt_money(doc.total_taxes_and_charges or 0, currency=doc.currency) }}
        </td>
      </tr>
      <tr class="grand-total">
        <td></td>
        <td><strong>Grand Total</strong></td>
        <td class="rt">
          {{ frappe.utils.fmt_money(doc.grand_total, currency=doc.currency) }}
        </td>
      </tr>
    </table>
  </div>

  {% if doc.in_words %}
    <p><strong>Amount in Words:</strong> {{ doc.in_words }}</p>
  {% endif %}

  {% if doc.tc_name %}
    {% set terms_doc = frappe.get_doc("Terms and Conditions", doc.tc_name) %}
    <div class="section">
      <div class="section-title">Terms & Conditions</div>
      <p style="font-size:10px; text-align:justify;">
        {{ terms_doc.terms|striptags }}
      </p>
    </div>
  {% endif %}

  <div class="footer-note">
    Thank you for the opportunity to quote. Please contact us for any clarification.
  </div>

</div>
