html:
{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}

<div class="main-container">
  <!-- Custom Letterhead -->
  <table style='width: 100%; margin: 0; padding: 0; border-collapse: collapse; font-family: "Inter", sans-serif; margin-bottom: 8px;'>
    <tbody>
      <tr>
        <td style="width: 130px; margin: 0; padding: 0; vertical-align: top;">
          {% set company_image = frappe.db.get_value('Company', doc.company, 'company_logo') %}
          {% if company_image %}
            <img src="{{ company_image }}" style="width: 130px; height: 50px; object-fit: contain; display: block; margin: 0; padding: 0;">
          {% endif %}
        </td>
        <td style="text-align: right; margin: 0; padding: 0; vertical-align: top; line-height: 1.2;">
          <div style="font-size: 13px; font-weight: 700; margin: 0 0 2px 0; color: #000;">
            {{ doc.company }}
          </div>

          {% set address = frappe.get_all("Address", filters={"link_doctype": "Company", "link_name": doc.company}, fields=["address_line1", "address_line2", "city", "country"], limit=1) or [] %}
          {% if address %}
            <div style="margin: 0 0 2px 0; font-size: 11px; line-height: 1.2; color: #000;">
              {{ address[0].address_line1 or "" }}{% if address[0].address_line2 %}, {{ address[0].address_line2 }}{% endif %}<br>
              {{ address[0].city or "" }}{% if address[0].country %}, {{ address[0].country }}{% endif %}
            </div>
          {% endif %}

          <div style="margin: 0; font-size: 11px; line-height: 1.2; color: #000;">
            {% set phone = frappe.db.get_value("Company", doc.company, "phone_no") %}
            {% set email = frappe.db.get_value("Company", doc.company, "email") %}
            {% set tax_id = frappe.db.get_value("Company", doc.company, "tax_id") %}

            {% if phone %}{{ phone }}{% endif %}{% if phone and email %} | {% endif %}{% if email %}{{ email }}{% endif %}<br>
            {% if tax_id %}Tax ID: {{ tax_id }}{% endif %}
          </div>
        </td>
      </tr>
    </tbody>
  </table>

  {%- set heading_label = "Sales Invoice" -%}

  {%- set cust_addr = frappe.get_all("Address", filters={"link_name": doc.customer, "link_doctype": "Customer"}, fields=["address_line1", "address_line2", "city", "country"], limit=1) -%}

  <style>
  {%- set border_inset = 0 -%}
  {%- set content_inner_padding = 3 -%}

  html, body { margin:0; padding:0; height:100%; font-family: Arial, sans-serif; font-size: 13px; }

  .print-format {
    position:relative;
    background:transparent !important;
    margin:0 !important;
    padding:0;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .page-border {
    position:fixed;
    top:{{ border_inset }}mm;
    left:{{ border_inset }}mm;
    right:{{ border_inset }}mm;
    bottom:{{ border_inset }}mm;
    border:1px solid #000;
    z-index:0;
    pointer-events:none;
  }

  .main-container {
    position:relative;
    z-index:1;
    padding:{{ content_inner_padding }}mm;
    font-family: Arial, sans-serif;
    font-size: 13px;
  }

  .print-heading,
  .print-heading + div { display:none !important; }

  .heading-label {
    font-size:18px;
    font-weight:600;
    text-align:center;
    color:#222;
    margin-bottom:1px;
    letter-spacing:1px;
    text-transform:uppercase;
    font-family: Arial, sans-serif;
  }

  .section { margin-bottom:1px; }

  .section-title {
    font-size:13px;
    font-weight:600;
    border-bottom:1px solid #000;
    color:#000;
    margin-bottom:2px;
    padding-bottom:1px;
    font-family: Arial, sans-serif;
  }

  .letter-head {
    margin-bottom:2mm !important;
  }

  .section:first-of-type {
    margin-top:0;
  }

  .info-table, .job-table, .items-table, .summary-table { width:100%; border-collapse:collapse; }
  .info-table td { 
    padding:2px 0; 
    vertical-align:top; 
    font-size: 13px;
    font-family: Arial, sans-serif;
  }

  .items-table th {
    background:#fff;
    color:#000;
    border-bottom:1px solid #000;
    padding:3px 4px;
    font-weight:700;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .items-table td {
    padding:3px 4px;
    border-bottom:none;
    vertical-align:top;
    line-height:1.15;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .summary-table td {
    padding:3px 4px;
    border-bottom:1px solid #f0f3f5;
    line-height:1.1;
    font-size:13px;
    font-family: Arial, sans-serif;
  }

  .rt { text-align:right; }
  .cn { text-align:center; }
  .grand-total td { 
    border-top: 2px solid #000; 
    border-bottom: 2px solid #000; 
    font-weight:700; 
  }

  .footer-note {
    font-size:13px;
    color:#000;
    text-align:center;
    margin-top:8px;
    font-family: Arial, sans-serif;
  }

  .item-name { color:#000; font-weight:500; }
  .item-desc { color:#000; }
  .item-description { white-space:normal; word-wrap:break-word; line-height:1.05; }

  .section:last-of-type { margin-bottom:4px; }
  </style>

  <div class="page-border"></div>

  <div class="heading-label">{{ heading_label }}</div>

  <!-- Customer Information -->
  <div class="section">
    <table class="info-table">
      <tr>
        <td style="width: 50%;">
          <strong>To:</strong><br>
          <strong>{{ doc.customer }}</strong><br>
          {% if cust_addr %}
            {{ cust_addr[0].address_line1 or "" }}<br>
            {% if cust_addr[0].address_line2 %}{{ cust_addr[0].address_line2 }}<br>{% endif %}
            {{ cust_addr[0].city or "" }}, {{ cust_addr[0].country or "" }}<br>
          {% endif %}
        </td>
        <td class="rt" style="width: 50%;">
          Invoice No.: <strong>{{ doc.name }}</strong><br>
          Date: {{ frappe.utils.formatdate(doc.date_created or doc.posting_date, "dd-MMM-yy") }}<br>
          Route: {{ doc.route }}<br>
          Trip Direction: {{ doc.trip_direction }}<br>
          Cargo Type: {{ doc.cargo_type }}
        </td>
      </tr>
    </table>
  </div>

  <!-- Invoice Items -->
  <div class="section">
    <div class="section-title">Invoice Items</div>
    <table class="items-table">
      <thead>
        <tr>
          <th class="cn" style="width:5%">#</th>
          <th style="width:70%">Description</th>
          <th class="rt" style="width:7%">Qty</th>
          <th class="rt" style="width:10%">Rate</th>
          <th class="rt" style="width:13%">Amount</th>
        </tr>
      </thead>
      <tbody>
        {% for item in doc.trip_bulk_sales_invoice_item %}
        <tr>
          <td class="cn">{{ loop.index }}</td>
          <td class="item-description">
            {{ item.charge }}{% if item.description %} - {{ item.description|striptags|replace('\n', ' ')|replace('\r', ' ') }}{% endif %}{% if item.truck %} - {{ item.truck }}{% endif %}{% if item.sales_invoice %} Ref: {{ item.sales_invoice }}{% endif %}
          </td>
          <td class="rt">{{ item.qty }}</td>
          <td class="rt">{{ frappe.utils.fmt_money(item.rate) }}</td>
          <td class="rt">{{ frappe.utils.fmt_money(item.amount) }}</td>
        </tr>
        {% endfor %}
      </tbody>
    </table>
  </div>

  <!-- Summary -->
  <div class="section">
    <div class="section-title">Summary</div>
    <table class="summary-table">
      <tr>
        <td style="width: 65%;"></td>
        <td style="width: 20%;"><strong>Sub Total</strong></td>
        <td class="rt" style="width: 15%; white-space: nowrap;">{{ frappe.utils.fmt_money(doc.sub_total) }}</td>
      </tr>
      <tr>
        <td style="border-bottom: none;"></td>
        <td style="border-bottom: none;"><strong>VAT</strong></td>
        <td class="rt" style="white-space: nowrap; border-bottom: none;">
          {{ frappe.utils.fmt_money(doc.vat) }}
        </td>
      </tr>
      <tr class="grand-total">
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"></td>
        <td style="border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>Grand Total</strong></td>
        <td class="rt" style="white-space: nowrap; border-top: 2px solid #000; border-bottom: 2px solid #000; padding-top: 4px; padding-bottom: 4px;"><strong>{{ frappe.utils.fmt_money(doc.grand_total) }}</strong></td>
      </tr>
    </table>
  </div>

  <!-- Banking Details -->
  {% if doc.banking_profile %}
  {% set banking_details = frappe.get_doc("Banking Profile", doc.banking_profile) %}
  <div class="section">
    <div class="section-title">Banking Details</div>
    <table class="info-table">
      <tr>
        <td style="width: 50%;">
          Bank Name: {{ banking_details.bank_name }}<br>
          Account Name: {{ banking_details.account_name }}<br>
          Account Number: {{ banking_details.account_number }}
        </td>
        <td style="width: 50%;">
          {% if banking_details.branch %}Branch: {{ banking_details.branch }}<br>{% endif %}
          {% if banking_details.swift_code %}Swift Code: {{ banking_details.swift_code }}<br>{% endif %}
          Currency: {{ banking_details.currency }}
        </td>
      </tr>
    </table>
    <hr style="border: none; border-top: 1px solid #000; margin-top: 8px; margin-bottom: 8px;">
  </div>
  {% endif %}

  <div class="footer-note">
    Thank you for your business! If you have any questions, please contact us.
  </div>
