html:
{%- set no_default_print_heading = 1 -%}
{%- from "templates/print_formats/standard_macros.html" import add_header -%}
{{ add_header(0, 1, doc, letter_head, no_letterhead, print_settings) }}

{%- set heading_label = (
    "Proforma Invoice" if doc.docstatus == 0 else
    "Tax Invoice" if doc.docstatus == 1 else
    "Invoice"
) -%}

{%- set cust_addr = frappe.get_all("Address", filters={"link_name": doc.customer, "link_doctype": "Customer"}, fields=["address_line1", "address_line2", "city", "country"], limit=1) -%}

<style>
  body {
    font-family: 'Inter', sans-serif;
    font-size: 12px;
    line-height: 1.4;
    color: #000;
  }

  /* Prevent logo stretching in PDF output - matching quotation/sales invoice size */
  .letter-head img,
  .letter-head .logo,
  .letter-head .logo img,
  .header-logo img,
  .company-logo img {
    max-width: 160px !important;
    max-height: 90px !important;
    width: auto !important;
    height: auto !important;
    object-fit: contain !important;
    display: inline-block !important;
  }

  .lf  { text-align: left; }
  .cn  { text-align: center; }
  .rt  { text-align: right; }
  .sm  { font-size: small; }
  .lg  { font-size: large; }
  .b   { font-weight: bold; }

  .custom-heading {
    font-size: 20px;
    font-weight: bold;
    text-align: center;
    margin-top: -15px;
    margin-bottom: 20px;
    text-transform: uppercase;
    color: #000;
  }

  .print-heading,
  .print-heading + div {
    display: none !important;
  }

  .table, .table th, .table td {
    border: 1px solid #000;
    border-collapse: collapse;
    font-size: 12px;
    padding: 0px;
    color: #000;
    vertical-align: top;
  }

  .trip-table {
    font-size: 12px;
    line-height: 0.3;
    border-collapse: collapse;
    margin-top: 3px;
    border: 1px solid #888;
  }

  .trip-table td {
    padding: 0px 2px;
    vertical-align: top;
    border: 1px solid #ccc;
    line-height: 0.3;
    color: #000;
  }

  .trip-table td strong {
    font-weight: normal;
  }

  .items-table {
    border-collapse: collapse;
    width: 100%;
  }

  .items-table thead {
    display: table-header-group;
  }

  .items-table th {
    font-weight: bold;
    font-size: 13px;
    color: #000;
    vertical-align: top;
    border: 1px solid #000 !important;
  }

  .items-table td {
    color: #000;
    vertical-align: top;
    border: 1px solid #000 !important;
  }

  .summary-table {
    border-collapse: collapse;
    width: 100%;
  }

  .summary-table thead {
    display: table-header-group;
  }

  .summary-table td {
    border: 1px solid #000;
    font-size: 12px;
    padding: 2px;
  }

  .section-title {
    margin: 0;
    font-size: 13px;
    font-weight: bold;
  }

  .sm strong {
    font-weight: normal;
  }
</style>

<!-- ✅ HEADER SECTION -->
<div class="page-header">
  <div class="custom-heading">{{ heading_label }}</div>

  <table style="width: 100%; border-collapse: collapse; font-size: 12px; line-height: 0;">
    <colgroup>
      <col style="width: 50%;">
      <col style="width: 20%;">
      <col style="width: 30%;">
    </colgroup>
    <tr><td><strong>To:</strong></td><td><strong>Invoice No.:</strong></td><td style="white-space: nowrap;"><strong>{{ doc.name }}</strong></td></tr>
    <tr><td><strong>{{ doc.customer_name }}</strong></td><td>Posting Date:</td><td style="white-space: nowrap;">{{ frappe.utils.formatdate(doc.posting_date, "dd-MMM-yy") }}</td></tr>
    <tr><td>{% if cust_addr %}{{ cust_addr[0].address_line1 or "" }}{% endif %}</td><td>Due Date:</td><td style="white-space: nowrap;">{{ frappe.utils.formatdate(doc.due_date, "dd-MMM-yy") }}</td></tr>
    <tr><td>{% if cust_addr %}{{ cust_addr[0].address_line2 or "" }}{% endif %}</td><td>Currency:</td><td style="white-space: nowrap;">{{ doc.currency }}</td></tr>
    <tr><td>{% if cust_addr %}{{ cust_addr[0].city or "" }}, {{ cust_addr[0].country or "" }}{% endif %}</td><td>Status:</td><td style="white-space: nowrap;">{{ doc.status }}</td></tr>
    <tr><td>Tax ID No: {{ frappe.db.get_value("Customer", doc.customer, "tax_id") or "-" }}</td><td>Outstanding:</td><td style="white-space: nowrap;">{{ doc.currency }} {{ "{:,.2f}".format(doc.outstanding_amount or 0) }}</td></tr>
  </table>
</div>

<!-- ✅ MAIN CONTENT -->
<p class="section-title" style="margin-top: 5px;"><strong>Trip Details</strong></p>

{% if doc.trip_reference %}
  {% set trip = frappe.get_doc("Trip", doc.trip_reference) %}
  <table class="trip-table" style="width: 100%; table-layout: fixed;">
    <colgroup>
      <col style="width: 15%;">
      <col style="width: 30%;">
      <col style="width: 20%;">
      <col style="width: 35%;">
    </colgroup>
    <tr><td><strong>Origin:</strong></td><td>{{ trip.origin }}</td><td><strong>Trip Reference:</strong></td><td>{{ trip.name }}</td></tr>
    <tr><td><strong>Destination:</strong></td><td>{{ trip.destination }}</td><td><strong>Client Reference:</strong></td><td>{{ trip.customer_reference or "-" }}</td></tr>
    <tr><td><strong>Drop Off At:</strong></td><td>{{ trip.drop_off_at }}</td><td><strong>Cargo Type:</strong></td><td>{{ trip.cargo_type }}</td></tr>
    <tr><td><strong>Horse:</strong></td><td>{{ trip.truck }}</td><td><strong>Cargo Desc.:</strong></td><td>{{ trip.cargo_description }}</td></tr>
    <tr><td><strong>Trailer:</strong></td><td>{{ trip.trailer }}</td><td><strong>Container No:</strong></td><td>{{ trip.container_number or "-" }}</td></tr>
  </table>
{% endif %}

<p class="section-title" style="margin-top: 20px;"><strong>Invoice Item Details</strong></p>

{% set vat_rate = frappe.db.get_value("Sales Taxes and Charges", {"parent": doc.name}, "rate") or 0 %}
<table class="table items-table" style="margin-top: 3px;">
  <thead>
    <tr>
      <th style="width: 5%;">Sr.</th>
      <th style="width: 14%;">Item Name</th>
      <th style="width: 45%;">Description</th>
      <th class="rt" style="width: 13%;">Amount (Excl.)</th>
      <th class="rt" style="width: 10%;">VAT</th>
      <th class="rt" style="width: 13%;">Amount (Incl.)</th>
    </tr>
  </thead>
  <tbody>
    {% for item in doc.items %}
    <tr>
      <td class="rt">{{ item.idx }}</td>
      <td>{{ item.item_name }}</td>
      <td>{{ item.description or "-" }}</td>
      <td class="rt">{{ "{:,.2f}".format(item.amount) }}</td>
      <td class="rt">{{ "{:,.2f}".format(item.amount * (vat_rate / 100)) }}</td>
      <td class="rt">{{ "{:,.2f}".format(item.amount * (1 + (vat_rate / 100))) }}</td>
    </tr>
    {% endfor %}
  </tbody>
</table>

<!-- ✅ SUMMARY TABLE -->
<table class="summary-table" style="width: 100%; margin-top: 3px; margin-bottom: 3px;">
  <thead>
    <tr style="display: none;"><th></th><th></th><th></th></tr>
  </thead>
  <tbody>
    <tr>
      <td rowspan="3" style="width: 65%; border: 1px solid #000; padding: 4px; vertical-align: top;">
        {% if doc.payment_terms_template %}
          <strong>Payment Terms:</strong> {{ doc.payment_terms_template }}
        {% endif %}
      </td>
      <td style="width: 15%;">Sub Total</td>
      <td class="rt" style="width: 20%;">{{ doc.currency }} {{ "{:,.2f}".format(doc.total or 0) }}</td>
    </tr>
    <tr>
      <td style="width: 15%;">VAT</td>
      <td class="rt" style="width: 20%;">
        {% if doc.total_taxes_and_charges %}
          {{ doc.currency }} {{ "{:,.2f}".format(doc.total_taxes_and_charges or 0) }}
        {% else %}
          -
        {% endif %}
      </td>
    </tr>
    <tr>
      <td style="width: 15%;"><strong>Grand Total</strong></td>
      <td class="rt" style="width: 20%;"><strong>{{ doc.currency }} {{ "{:,.2f}".format(doc.grand_total or 0) }}</strong></td>
    </tr>
  </tbody>
</table>

<p class="sm rt" style="margin-top: 3px;">Amount in Words: {{ doc.in_words or "" }}</p>
